    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
                
        temer.getDia(dia.getValue().toString());

        int horas = 24, minutos = 60, segundos = 60;
        String choras = dia.getValue().toString();
        int Vuel = Integer.parseInt(choras);
        int horai = Vuel * horas;
        int min = horai * minutos;
        int seg = min *segundos;
        
        String CadenaHora= Integer.toString(horai);
        String CadenaMinuto= Integer.toString(min);
        String CadenaSegundo= Integer.toString(seg);
        
        String dias = dia.getValue().toString() + " dias " + " tienen " + CadenaHora + " horas * "+ CadenaMinuto + " minutos * " + CadenaSegundo + " segundos" ;
        mensagge.setText(dias );
        jhora.setText(CadenaHora);
        jmin.setText(CadenaMinuto);
        jseg.setText(CadenaSegundo);


        
    }//GEN-LAST:event_jButton1ActionPerformed

